    private void issueBookActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_issueBookActionPerformed
        Connection con = null;
        PreparedStatement ps = null;
        ResultSet rs = null;
        try {
            con = databaseHelper.connect();
            String sqlQuery = "insert into bookIssueDetails (studentid, studentname, bookid, bookname) values (?,?,?,?)";
            ps = con.prepareStatement(sqlQuery);

            if(bookIssueStudentName.getText().equals("")||bookIssueBookName.getText().equals(""))
                JOptionPane.showMessageDialog(null, "enter correct Id");
            else{
                ps.setInt(1, Integer.parseInt(bookIssueStudentId.getText()));
                ps.setString(2, bookIssueStudentName.getText());
                ps.setInt(3, Integer.parseInt(bookIssueBookId.getText()));
                ps.setString(4, bookIssueBookName.getText());
                ps.execute();
                JOptionPane.showMessageDialog(null, "book issued successfully");
            }
        } catch(Exception e){
            e.printStackTrace();
        } finally{
            try{
                rs.close();
                ps.close();
                con.close();
            } catch(Exception e){
                e.printStackTrace();
            }
        }
    }//GEN-LAST:event_issueBookActionPerformed

