    private void bookIssueStudentIdKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_bookIssueStudentIdKeyReleased
        Connection con = null;
        PreparedStatement ps = null;
        ResultSet rs = null;
        try {
            con = databaseHelper.connect();
            String sqlQuery = "SELECT * FROM studentdetails WHERE stdId = ?";
            ps = con.prepareStatement(sqlQuery);

            if(bookIssueStudentId.getText().equals(""))
                ps.setInt(1, 0);
            else{
                ps.setInt(1, Integer.parseInt(bookIssueStudentId.getText()));
            }

            // Execute the query and store the result in a ResultSet
            rs = ps.executeQuery();

            // Process the query result (e.g., print the results)
            if (rs.next()) {
                bookIssueStudentName.setText(rs.getString(2));
                bookIssueFName.setText(""+rs.getString(3));
                bookIssueCourse.setText(rs.getString(4));
                bookIssueYear.setText(""+rs.getInt(5));
                bookIssueSem.setText(""+rs.getInt(6));
            } else{
                bookIssueStudentName.setText("");
                bookIssueFName.setText("");
                bookIssueCourse.setText("");
                bookIssueYear.setText("");
                bookIssueSem.setText("");
            }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "enter integers only");
        } catch(Exception e){
            e.printStackTrace();
        }finally{
            try{
                rs.close();
                ps.close();
                con.close();
            } catch(Exception e){
                e.printStackTrace();
            }
        }        
    }//GEN-LAST:event_bookIssueStudentIdKeyReleased

