    private void bookIssueBookIdKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_bookIssueBookIdKeyReleased
        Connection con = null;
        PreparedStatement ps = null;
        ResultSet rs = null;
        try {
            con = databaseHelper.connect();
            String sqlQuery = "SELECT * FROM bookdetails WHERE bookId = ?";
            ps = con.prepareStatement(sqlQuery);
            
            if(bookIssueBookId.getText().equals(""))
                ps.setInt(1, 0);
            else{
                ps.setInt(1, Integer.parseInt(bookIssueBookId.getText()));
            }

            // Execute the query and store the result in a ResultSet
            rs = ps.executeQuery();

            // Process the query result (e.g., print the results)
            if (rs.next()) {
                bookIssueBookName.setText(rs.getString(2));
                bookIssueEdition.setText(""+rs.getInt(3));
                bookIssuePublisher.setText(rs.getString(4));
                bookIssueStock.setText(""+rs.getInt(5));
            } else{
                bookIssueBookName.setText("");
                bookIssueEdition.setText("");
                bookIssuePublisher.setText("");
                bookIssueStock.setText("");
            }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "enter integers only");
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e);
        } finally{
            try{
                rs.close();
                ps.close();
                con.close();
            } catch(Exception e){
                e.printStackTrace();
            }
        }
    }//GEN-LAST:event_bookIssueBookIdKeyReleased

